import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

const SellProducts = () => {
    const [productName, setProductName] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState(null);

    const isAuthenticated = useSelector(state => state.auth.isAuthenticated); // Replace with your auth state
    const navigate = useNavigate();

    // Redirect if not authenticated
    if (!isAuthenticated) {
        navigate('/login');
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle the form submission
        const formData = new FormData();
        formData.append('productName', productName);
        formData.append('price', price);
        formData.append('description', description);
        formData.append('image', image);

        // Simulate a submission
        console.log('Product submitted:', { productName, price, description, image });
        alert('Product uploaded successfully!');
    };

    return (
        <div className="container my-5">
            <h2 className="text-center mb-4">Sell Your Product</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group mb-3">
                    <label>Product Name</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        value={productName} 
                        onChange={(e) => setProductName(e.target.value)} 
                        required 
                    />
                </div>
                <div className="form-group mb-3">
                    <label>Price</label>
                    <input 
                        type="number" 
                        className="form-control" 
                        value={price} 
                        onChange={(e) => setPrice(e.target.value)} 
                        required 
                    />
                </div>
                <div className="form-group mb-3">
                    <label>Description</label>
                    <textarea 
                        className="form-control" 
                        value={description} 
                        onChange={(e) => setDescription(e.target.value)} 
                        rows="3" 
                        required
                    ></textarea>
                </div>
                <div className="form-group mb-3">
                    <label>Upload Image</label>
                    <input 
                        type="file" 
                        className="form-control" 
                        onChange={(e) => setImage(e.target.files[0])} 
                        required 
                    />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    );
};

export default SellProduct;
